body {
  margin: 0;
  background-image: url("e3c50f902cef0eada8f2227d90dedb68.jpg");
  background-size: cover;
  background-repeat: no-repeat;
  background-color: grey;
}

.headContainer {
  display: flex;
  justify-content: center;
  font-family: "Bungee Inline", cursive;
  font-size: 50px;
}

.container {
  display: flex;
  justify-content: center;
  align-items: center;
  height: 750px;
  width: 100%;
}
.container .gameboard {
  float: right;
  display: grid;
  grid-template-columns: repeat(3, 150px);
  grid-template-rows: repeat(3, 150px);
  height: 500px;
  width: 500px;
  margin: 50px;
}
.container .levelContainer {
  display: flex;
  justify-content: center;
  align-items: center;
  font-family: "Bungee Inline", cursive;
  font-display: 30px;
  grid-column: 2/3;
  grid-row: 2;
}
.container .greenLight {
  height: 150px;
  width: 150px;
  background-color: #00f500;
  opacity: 0.3;
  border-radius: 50%;
  grid-column: 1/3;
  grid-row: 2;
}
.container .redLight {
  height: 150px;
  width: 150px;
  background-color: red;
  opacity: 0.3;
  border-radius: 50%;
  grid-column: 2/3;
  grid-row: 1;
}
.container .yellowLight {
  height: 150px;
  width: 150px;
  background-color: yellow;
  opacity: 0.3;
  border-radius: 50%;
  grid-column: 3/3;
  grid-row: 2;
}
.container .blueLight {
  height: 150px;
  width: 150px;
  background-color: #0000f5;
  opacity: 0.3;
  border-radius: 50%;
  grid-column: 2/3;
  grid-row: 3;
}
.container .buttonContainer {
  display: flex;
  justify-content: center;
  flex-direction: column;
  text-align: center;
}
.container .buttonContainer #restartButton {
  margin-top: 20px;
}

button {
  display: flex;
  justify-content: center;
  font-family: "Bungee Inline", cursive;
  font-size: 40px;
  padding: 10px;
  margin-right: 100px;
  align-items: center;
  background-image: linear-gradient(to bottom, #eb94d0, #2079b0);
}

.shade {
  opacity: 1 !important;
  border-color: whitesmoke;
  box-shadow: 0 0 10rem whitesmoke;
}

.greenLight:hover {
  opacity: 1;
  border-color: whitesmoke;
  box-shadow: 0 0 10rem whitesmoke;
}

.redLight:hover {
  opacity: 1;
  border-color: whitesmoke;
  box-shadow: 0 0 10rem whitesmoke;
}

.yellowLight:hover {
  opacity: 1;
  border-color: whitesmoke;
  box-shadow: 0 0 10rem whitesmoke;
}

.blueLight:hover {
  opacity: 1;
  border-color: whitesmoke;
  box-shadow: 0 0 10rem whitesmoke;
}

button:hover {
  border: 1px solid blue;
  color: blue;
  border-color: whitesmoke;
  box-shadow: 0 0 10rem whitesmoke;
}
