body {
  margin: 0;
  background-color: #efefef;
  border: solid black 1px;
}

.headContainer {
  display: flex;
  justify-content: center;
}

.container {
  display: flex;
  justify-content: center;
  height: 750px;
  width: auto;
  border: solid black 1px;
}
.container .gameboard {
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  grid-auto-rows: minmax(100px, auto);
  height: 500px;
  width: 500px;
  margin: 50px;
}
.container .greenLight {
  height: 150px;
  width: 150px;
  background-color: rgba(0, 245, 0, 0.3);
  border-radius: 50%;
  grid-column: 1/3;
  grid-row: 2;
}
.container .redLight {
  height: 150px;
  width: 150px;
  background-color: rgba(255, 0, 0, 0.3);
  border-radius: 50%;
  grid-column: 2/3;
  grid-row: 1;
}
.container .yellowLight {
  height: 150px;
  width: 150px;
  background-color: rgba(245, 245, 0, 0.3);
  border-radius: 50%;
  grid-column: 3/3;
  grid-row: 2;
}
.container .blueLight {
  height: 150px;
  width: 150px;
  background-color: rgba(0, 0, 245, 0.3);
  border-radius: 50%;
  grid-column: 2/3;
  grid-row: 3;
}
.container .buttons {
  display: flex;
  justify-content: center;
}
