generated.css:
body {
  margin: 0;
  background-color: #efefef;
  border: solid black 1px;
}

.headContainer {
  display: flex;
  justify-content: center;
}

.container {
  display: flex;
  justify-content: center;
  align-items: center;
  height: 750px;
  width: auto;
  border: solid black 1px;
}
.container .gameboard {
  display: grid;
  grid-template-columns: repeat(3, 150px);
  grid-template-rows: repeat(3, 150px);
  height: 500px;
  width: 500px;
  margin: 50px;
}
.container .greenLight {
  height: 150px;
  width: 150px;
  background-color: #00f500;
  opacity: 0.3;
  border-radius: 50%;
  grid-column: 1/3;
  grid-row: 2;
}
.container .redLight {
  height: 150px;
  width: 150px;
  background-color: red;
  opacity: 0.3;
  border-radius: 50%;
  grid-column: 2/3;
  grid-row: 1;
}
.container .yellowLight {
  height: 150px;
  width: 150px;
  background-color: yellow;
  opacity: 0.3;
  border-radius: 50%;
  grid-column: 3/3;
  grid-row: 2;
}
.container .blueLight {
  height: 150px;
  width: 150px;
  background-color: #0000f5;
  opacity: 0.3;
  border-radius: 50%;
  grid-column: 2/3;
  grid-row: 3;
}

.buttonContainer {
  display: flex;
  justify-content: center;
  align-items: center;
}
.buttonContainer #startButton {
  margin-right: 50px;
}

.buttons {
  display: flex;
  justify-content: center;
  align-items: center;
}

.shade {
  opacity: 1 !important;
}
